            "استهداف عدة شرائح في نفس الوقت",
            "التخبط في القرارات لادارة المشروع",
            "قلة الخبرة لدى فريق العمل",
            "تأسيس المشروع بمعدات ذات جودة منخفضة",
            "ارتفاع تكاليف المواد الخام بداية المشروع بالتزامات مالية عالية",
            "عدم وجود رأس مال كافي لبداية المشروع",
            "عدم احتساب راس المال العامل",
            "فكرة المشروع ليست جديدة في السوق",
            "عدم وجود عملاء سابقين",
            "عدم وجود تمويل للمشروع",
            "اخرى"
            ],
            type: "checkBox",
            page: 4,
        },


    ]);

    let [currentPage, setCurrentPage] = useState(4);

    useEffect(() => {

        console.log(answers)

    }, [answers])

    function onSelectAnswerChange(questionIndex, item) {

        let filteredQuestions = answers.filter((item) => item.question !== questionIndex);

        setAnswers([...filteredQuestions, { question: questionIndex, answer: item }])
    }

    function onCheckBoxAnswerSelected(questionIndex, item) {

        let currentAnswer = answers.find((item) => item.question === questionIndex);

        let filteredQuestions = answers.filter((item) => item.question !== questionIndex);

        let filteredAnswer = []

        if (currentAnswer === undefined)
            filteredAnswer = [item];
        else
            filteredAnswer = [...currentAnswer.answer, item];

        let result = [...filteredQuestions, { question: questionIndex, answer: filteredAnswer }]

        setAnswers(result)

    }

    function onTextInputChanged(text, questionIndex) {

        let filteredQuestions = answers.filter((item) => item.question !== questionIndex);

        let result = [...filteredQuestions, { question: questionIndex, answer: text }]

        setAnswers(result)

    }


    function getSelectInput(question, answer, questionIndex) {


        return <div className="form-input">
            <select class="form-select" aria-label="Default select example" onChange={(item) => onSelectAnswerChange(questionIndex, item.target.value)}>
                <option index={0} selected>{question}</option>
                {answer.map((option, index) => <option value={index}>{option.trim()}</option>)}
            </select>
        </div>
    }

    function getCheckInput(question, answer, questionIndex) {

        return <>
            <div style={{ textAlign: "right", paddingRight: 15, }}><label>{question}</label></div>
            {answer.map((option, index) => <div className="form-check form-input">
                <input className="form-check-input" type="checkbox" onChange={() => onCheckBoxAnswerSelected(questionIndex, index)} />
                <label className='check-label'>
                    {option}
                </label>
            </div>)}
        </>

    }

    function getTextInput(question, answer, questionIndex) {

        return <div className="form-input">
            <label style={{ padding: 10, }}>{question}</label>
            <input className="form-control" type="text" onChange={(item) => onTextInputChanged(item.target.value, questionIndex)} />
        </div>


    }


    return (
        <div style={{ textAlign: "center" }}>
            <header className="App-header">
                معلومات المشروع
            </header>

            <form className='infoForm col-sm-10 col-md-10 col-lg-10'>
                <fieldset>
                    {/* <div className="mb-3 form-input">
                        <input type="text" className="form-control" placeholder="الاسم" />
                    </div> */}

                    <FormHeader currentPage={currentPage} />

                    {questions.map(({ question, answers, page, type }, index) => {
                        // console.log(question, answers)
                        if (page === currentPage) {

                            if (type === "select") {
                                return getSelectInput(question, answers, index)
                            } else if (type === "checkBox") {
                                return getCheckInput(question, answers, index)
                            } else {
                                return getTextInput(question, answers, index)
                            }

                        }
                    })}

                    <div className="formBottom">

                        {(currentPage > 1) &&
                            <Button variant="primary" className="nextButton" onClick={() => setCurrentPage(currentPage - 1)}>
                                السابق
                            </Button>
                        }

                        <Button variant="success" className="nextButton" onClick={() => setCurrentPage(currentPage + 1)}>
                            التالي
                        </Button>

                    </div>

                </fieldset>
            </form>

        </div>
    )
}

export default FeasibilityInfo
