import React, { useEffect, useState } from 'react'
import { Button } from "react-bootstrap"
import FormHeader from './FormHeader';

function FeasibilityInfo() {

    let [answers, setAnswers] = useState([]);


    let [questions, setQuestions] = useState([
        {
            question: "حدد القطاع", answers: [
                " الانشطة الاخرى للتعدين واستعلال المحاجر",
                " انشطة خدمات الدعم التعديل",
                " صنع  المنتجات الغذائية",
                " صنع المشروبات",
                " صنع المنسوجات",
                " صنع الملبوسات",
                " صنع المنتجات الجلدية وذات الصلة",
            ],
            type: "select",
            page: 1,
        },
        {
            question: "حدد نشاط المشروع", answers: [
                "صنع الكاكاو والشوكلاته والحلويات السكرية",
                "صنع الكاكاو في شكل معجون او مسحوق او كتل",
                "صنع زبدة الكاكاو او دهن او زيت الكاكاو",
                "انشطة اخرى لصناعات الكاكاو",
                "صناعة المعكرونة بكافة انواعها",
            ],
            type: "select",
            page: 1,
        },

    ]);
    let [currentPage, setCurrentPage] = useState(1);


    const somthing = {
        question: "حدد القطاع", answers: [
            " الانشطة الاخرى للتعدين واستعلال المحاجر",
            " انشطة خدمات الدعم التعديل",
            " صنع  المنتجات الغذائية",
            " صنع المشروبات",
            " صنع المنسوجات",
            " صنع الملبوسات",
            " صنع المنتجات الجلدية وذات الصلة",
        ],
        page: 1,
    }

    useEffect(() => {

        console.log(answers)

    }, [answers])

    function onSelectAnswerChange(questionIndex, item) {

        let filteredAnswers = answers.filter((item) => item.question !== questionIndex);

        setAnswers([...filteredAnswers, { question: questionIndex, answer: item }])
    }


    function getSelectInput(question, answer, questionIndex) {


        return <div className="form-input">
            <select class="form-select" aria-label="Default select example" onChange={(item) => onSelectAnswerChange(questionIndex, item.target.value)}>
                <option index={0} selected>{question}</option>
                {answer.map((option, index) => <option value={index}>{option.trim()}</option>)}
            </select>
        </div>
    }

    function getCheckInput(question, answer, questionIndex) {

        return <div className="form-check form-input">
            <input className="form-check-input" type="checkbox" value="" />
            <label className='check-label'>
                {question}
            </label>
        </div>

    }


    return (
        <div style={{ textAlign: "center" }}>
            <header className="App-header">
                معلومات المشروع
            </header>

            <form className='infoForm col-sm-10 col-md-10 col-lg-10'>
                <fieldset>
                    {/* <div className="mb-3 form-input">
                        <input type="text" className="form-control" placeholder="الاسم" />
                    </div> */}

                    <FormHeader />

                    {questions.map(({ question, answers, page }, index) => {
                        console.log(index);
                        // console.log(question, answers)
                        if (page === currentPage) {

                            if (type === "select") {
                                return getSelectInput(question, answers, index)
                            } else {
                                return getCheckInput(question, answers, index)
                            }

                        }
                    })}

                    <div className="formBottom">

                        <Button variant="primary" className="nextButton" onClick={() => setCurrentPage(currentPage + 1)}>
                            التالي
                        </Button>

                    </div>

                </fieldset>
            </form>

        </div>
    )
}

export default FeasibilityInfo
