import React from 'react'
import { useLocation } from "react-router-dom";

function ResultScreen() {

  let params = useLocation();

  let result = params.state.message;

  function reFormatText(text){

    console.log(text.replaceAll("\n", '<br />'));

    document.querySelector("#resultedMessageText").innerHTML = text.replaceAll("\n", '<br />')    


  }

reFormatText(result);

  return (
    <div>
      <header className="App-header">
        نتيجة دراسة الجدوى
      </header>

      <div className="container">
        <p id="resultedMessageText" className="resultMessage">

        </p>
      </div>

    </div>
  )
}

export default ResultScreen
