import React from 'react'

function Chat() {

    let messages = [{ message: "Hello", type: "sender" }, { message: "Hi", type: "reciever" }];

    return (
        <div className="App">
            <header className="App-header">
                Chat
            </header>

            <div className="Container">

                <div className="chatBox">

                    {messages.map(({ message, time, type }) => {

                        let messageStyle = type == "sender" ? { justifyContent: "flex-start" } : { justifyContent: "flex-end" }

                        console.log(type)

                        return <div className="messageDiv" style={{justifyContent: "flex-end"}}>
                            <div className="messageBox">
                                {message}
                                <div className="whenSent">{time}</div>
                            </div>
                        </div>

                    })}


                </div>

            </div>


        </div>

    )
}

export default Chat
